import React, { useState }  from 'react'
// import { useInput } from './useInput/useInput'


const contents = [
    {
        name : 'HyoungBum',
        content : 'What is a sexy guy in the world'
    },
    {
        name : 'EunJi',
        content : 'what is a sexy girl in the world'
    }
]

const useTabs = (initialCurrentIndex, allTabs) => {
    const [currentIndex, setCurrentIndex] = useState(initialCurrentIndex);
    return {
        item: allTabs[currentIndex],
        changeItem: setCurrentIndex
    }
}
function App() {
    const [item, changeItem] = useTabs(0, contents);
    return (
        <>
            <div>
                {contents.map((person, index) => <button onClick={() => changeItem(index)}>{person.name}</button>)}
                <div>{item.content}</div>
            </div>
        </>
    );
}

export default App;
